# 숫자형 연산
# https://docs.python.org/3/library/stdtypes.html#numeric-types-int-float-complex

# 정수 선언
i = 77
i2 = -14
big_int = 77777777777777777777777777777777777777

# 정수 출력
print(i) # 77
print(i2) # -14
print(big_int) # 77777777777777777777777777777777777777

# 실수 선언
f = 0.9999
f2 = 3.141592
f3 = -3.9
f4 = 3 / 9

# 실수 출력
print(f) # 0.9999
print(f2) # 3.141592
print(f3) # -3.9
print(f4) # 0.3333333333333333

# 연산 실습
i1 = 39
i2 = 939
big_int1 = 123456789123456789012345678901234567890
big_int2 = 999999999999999999999999999999999999999
f1 = 1.234
f2 = 3.939

# +
print(i1 + i2) # 978
print(f1 + f2) # 5.173
print(big_int1 + big_int2) # 1123456789123456789012345678901234567889
print(i1 + f1, type(i1 + f1))  # 40.234 <class 'float'>

# -
print(i1 - i2) # -900
print(f1 - f2) # -2.705
print( big_int1 - big_int2) # -876543210876543210987654321098765432109
print(i1 - f1) # 37.766

# *
print(i1 * i2) # 36621
print(f1 * f2) # 4.860726
print(big_int1 * big_int2) # 123456789123456789012345678901234567889876543210876543210987654321098765432110
print(i1 * f1) # 48.126

# /
print(i2 / i1) # 24.076923076923077
print(f2 / f1) # 3.1920583468395463
print(big_int2 / big_int1) # 8.10000006561
print(i1 / f1) # 31.60453808752026
print(f1 / i1) # 0.03164102564102564

# //
print(i2 // i1) # 24
print(f2 // f1) # 3.0
print(big_int2 // big_int1) # 8
print(i1 // f1) # 31.0
print(f1 // i1) # 0.0

# %
print(i1 % i2) # 39
print(f1 % f2) # 1.234
print(big_int1 % big_int2) # 123456789123456789012345678901234567890
print(i1 % f1) # 0.7460000000000004
print(f1 % i1) # 1.234

# **
print(2 ** 3) # 8
print(i1 ** i2) # 
print(f1 ** f2) # 2.289235194260789
print(i1 ** f1) # 91.91231928197118
print(f1 ** i1) # 3641.5915127246435

print()

# 단항 연산자
# +=, -=, *=, /=
x = 100
x += 100 # y = y + 100
print(x) # 200

print()

# 형변환
# int, float, complex(복소수)
a = 5.
b = 4

print(type(a), type(b)) # <class 'float'> <class 'int'>
print(int(a)) # 5
print(float(b)) # 4.0
print(complex(3)) # (3+0j)
print(complex(True)) # (1+0j)
print(complex(False)) # (0j)
print(int(True)) # 1
print(int(False)) # 0
print(int('3')) # 3

print()

